import UIKit

class OnboardingPresenter: BasePresenter,OnboardingPresenterProtocol {
    
    
    weak internal var view: OnboardingViewProtocol?
    
    var interactor: OnboardingInteractorInputProtocol?
    
    private let router: OnboardingRouterProtocol
    
    private var state: OnboardingState = .Country
    
    private var dataSource: LabelCollectionViewDataSource?
    
    init(view: OnboardingViewProtocol, interactor: OnboardingInteractorInputProtocol?, router: OnboardingRouterProtocol,state: OnboardingState) {
        self.view = view
        self.interactor = interactor
        self.router = router
        self.state = state
    }
    func loadDetails() {
        guard let view = view else {
            return
        }
        switch self.state {
        case .Categories:
            dataSource = LabelCollectionViewDataSource(collection: view.topicsCollections, models: AppTargetConstant.Categories, delegate: self,state: .Categories)
            view.loadCategoriesLayout()
            break
        case .Country:
            dataSource = LabelCollectionViewDataSource(collection: view.topicsCollections, models: AppTargetConstant.Countries, delegate: self,state: .Country)
            view.loadCountriesLayout()
            break
        }
    }
    
    func goToNextPage() {
        guard let dataSource = dataSource else {
            return
        }
        switch self.state {
        case .Categories:
            self.interactor?.saveCategories(categories: dataSource.selectedArray)
            self.router.goToHome()
            break
        case .Country:
            if let str = dataSource.selectedArray.first {
                self.interactor?.saveCountry(code: str)
            }
            
            self.router.goToNext()
            break
        }
    }
    
}

extension  OnboardingPresenter:LabelCollectionViewDataSourceActions{
    func didSelect(model: CountryModel) {
        switch self.state {
        case .Country:
            self.goToNextPage()
            break
        case .Categories:
            if dataSource!.selectedArray.count > 2 {
                self.goToNextPage()
            }
            break
        }
    }
}

extension OnboardingPresenter: OnboardingInteractorOutputProtocol {
    
}

enum OnboardingState {
    case Country , Categories
}
